			tl1.font = "微软雅黑";
			tl1.bold = true;
			var tl2 = $.createComment("世界终结舞厅",{x:-93, y:20, fontsize:30, lifeTime:life, parent:title});			
			tl2.filters=[$.createGlowFilter(0x000000, 1, 5, 5), $.createBlurFilter(1, 1)];
			tl2.font = "微软雅黑";
			tl2.bold = true;
		interval(function(){
			title.scaleX -= 0.001;
			title.scaleY -= 0.001;
		}, 10, 600);
		},delay);
	}

